import React from "react";
import '../styles/BuyList.css'

function neatlyFormatPrice(price) {
  var newPriceStr = "";
  var forwardPositionCounter = 0;
  for (var i = price.toString().length - 1;i >= 0;i--) {
    if ((forwardPositionCounter + 1) % 3 == 0 && i != 0) {
      newPriceStr = "," + newPriceStr;
    }
    newPriceStr = price.toString()[i] + newPriceStr;
  }
  return newPriceStr;
}

function BuyItem({ image, address, price, type }) {
  return (
    <div className="buyItem">
      <div style={{ backgroundImage: `url(${image})` }}> </div>
      <h1> {address} </h1>
      <p> ${neatlyFormatPrice(price)} </p>
      <p> {type} </p>
    </div>
  );
}

export default BuyItem;
