import React from 'react'
import { useState } from 'react'

var minPriceTracker = 0;
var maxPriceTracker = 0;
var errorMessage = "Please enter at least one search criterion.";
var isSearchButtonDisabled = true;

function isNullOrEmpty(stringInput) {
  if (stringInput === null || stringInput.toString().trim() === "") {
    return true;
  } else {
    return false;
  }
}

function checkMinAndMax(event) {
  if (event.target.id == 'min_price') {
    minPriceTracker = event.target.value;
  }
  if (event.target.id == 'max_price') {
    maxPriceTracker = event.target.value;
  }
  console.log(minPriceTracker);
  console.log(maxPriceTracker);
  if (minPriceTracker == 0 && maxPriceTracker == 0) {
    errorMessage = "Please choose a minimum or maximum price."
    isSearchButtonDisabled = true;
    return;
  }
  if (minPriceTracker > maxPriceTracker) {
    errorMessage = "Maximum price must be greater than minumum price."
    isSearchButtonDisabled = true;
    return;
  }
  if (maxPriceTracker == 0) {
    errorMessage = "Maximum price must be greater than $0"
    isSearchButtonDisabled = true;
    return;
  }
  errorMessage=""
  isSearchButtonDisabled = false;
  return;
}

function search() {
  return (
    <div>
      <h1> Search </h1>

      <form id="search-form" method="POST">
        <label htmlFor="address">Address</label>
        <input name="address" id="address" placeholder="Address" type="text" />
        <label htmlFor="min_price">Minimum Price</label>
        <input name="min_price" id="min_price" placeholder="Minimum price" type="number" onBlur={checkMinAndMax} />
        <label htmlFor="max_price">Maximum Price</label>
        <input name="max_price" id="max_price" placeholder="Maximum price" type="number" onBlur={checkMinAndMax} />
        <label htmlFor="numBedrooms"># of Bedrooms</label>
        <select id="numBedrooms" name="numBedrooms">
            <option value="" selected disabled>Select Beds</option>
            <option value="noselection">No Selection</option>
            <option value="1">1 Bed</option>
            <option value="2">2 Beds</option>
            <option value="3">3 Beds</option>
            <option value="4">4 Beds</option>
            <option value="5">5 Beds</option>
        </select>
        <label htmlFor="numBathrooms"># of Bathrooms</label>
        <select id="numBathrooms" name="numBathrooms">
            <option value="" selected disabled>Select Beds</option>
            <option value="noselection">No Selection</option>
            <option value="1">1 Bed</option>
            <option value="2">2 Beds</option>
            <option value="3">3 Beds</option>
            <option value="4">4 Beds</option>
            <option value="5">5 Beds</option>
        </select>
        <div id="errorMessage">{errorMessage}</div>
        <button type="submit" disabled = {isSearchButtonDisabled}> Search </button>
      </form>
    </div>
  )
}

export default search
