import React, { useState } from 'react'
import { BuyList } from '../helpers/BuyList'
//import mysql from 'mysql'

function PropertySearchForm() {
  // var [state, setState] = useState({
  //   minPrice: "",
  //   maxPrice: "",
  //   errorMessage: "Please enter at least one search criterion",
  //   isSearchButtonDisabled: true
  // });
  //
  // const performChecks = () => {
  //   if (minPrice < 0) {
  //     minPrice = 0;
  //   }
  //   if (maxPrice < 0) {
  //     maxPrice = 0;
  //   }
  //   if (minPrice == 0 && maxPrice == 0) {
  //     errorMessage = "Please choose a minimum or maximum price."
  //     isSearchButtonDisabled = true;
  //   }
  //   else if (minPrice > maxPrice) {
  //     errorMessage = "Maximum price must be greater than minumum price."
  //     isSearchButtonDisabled = true;
  //   }
  //   else if (maxPrice == 0) {
  //     errorMessage = "Maximum price must be greater than $0"
  //     isSearchButtonDisabled = true;
  //   }
  //   else {
  //     errorMessage=""
  //     isSearchButtonDisabled = false;
  //   }
  // }

  var [minPrice, setMinPrice] = useState("");
  var [maxPrice, setMaxPrice] = useState("");
  var [minSize, setMinSize] = useState("");
  var [maxSize, setMaxSize] = useState("");
  var [bedrooms, setBedrooms] = useState("");
  var [bathrooms, setBathrooms] = useState("");
  var [address, setAddress] = useState("");
  var [errorMessage, setErrorMessage] = useState("Please enter at least one search criterion");
  var [isSearchButtonDisabled, setIsSearchButtonDisabled] = useState(true);

  const addressCheck = function(event) {
    setAddress(event.target.value);
    console.log(address);
    performChecks();
  }

  const bedroomsCheck = function(event) {
    if (event.target.value < 0) {
      event.target.value = 0;
    }
    setBedrooms(parseInt(event.target.value));
    performChecks();
  }

  const bathroomsCheck = function(event) {
    if (event.target.value < 0) {
      event.target.value = 0;
    }
    setBathrooms(parseInt(event.target.value));
    performChecks();
  }

  const performChecks = function() {
    if (address == "") {
      if (minPrice == 0 || minPrice == "") {
        if (maxPrice == 0 || maxPrice == "") {
          if (bedrooms == "") {
            if (bathrooms == "") {
              setErrorMessage("Please enter at least one search criterion.");
              setIsSearchButtonDisabled(true)
              return;
            }
          }
        }
      }
    }
    else if (bedrooms != "" && (bedrooms < 1 || bedrooms > 5)) {
      setErrorMessage("If choosing a number of bedrooms, please choose a number between 1 and 5.");
      setIsSearchButtonDisabled(true)
    }
    else if (bathrooms != "" && (bathrooms < 1 || bathrooms > 5)) {
      setErrorMessage("If choosing a number of bathrooms, please choose a number between 1 and 5.");
      setIsSearchButtonDisabled(true)
    }
    else if ((minPrice == 0 && maxPrice == 0) && (minPrice.toString() != '' && maxPrice.toString() != '')) {
      setErrorMessage("Please choose a minimum or maximum price.");
      setIsSearchButtonDisabled(true)
    }
    else if (minPrice > maxPrice) {
      setErrorMessage("Maximum price must be greater than minimum price.");
      setIsSearchButtonDisabled(true)
    }
    else if (maxPrice == 0 && maxPrice.toString() != '') {
      setErrorMessage("Maximum price must be greater than $0");
      setIsSearchButtonDisabled(true);
    }
    else {
      setErrorMessage("");
      setIsSearchButtonDisabled(false);
    }
  }

  const checkIfValueNegative = function(event) {
    if (event.target.value < 0) {
      event.target.value = 0;
    }
    if (event.target.id == "min_price") {
      setMinPrice(event.target.value);
    }
    if (event.target.id == "max_price") {
      setMaxPrice(event.target.value);
    }
    if (event.target.id == "min_size") {
      setMinSize(event.target.value);
    }
    if (event.target.id == "max_size") {
      setMaxSize(event.target.value);
    }
    performChecks();
  }

  const isNullOrEmpty = function(stringInput) {
    if (stringInput === null || (stringInput === undefined || stringInput.toString().trim() === "")) {
      return true;
    } else {
      return false;
    }
  }

  const performSearch = function() {
    var results = BuyList;
    if (!isNullOrEmpty(address)) {
      results = results.filter(function(property) {
        return property.address.toLowerCase().includes(address.toLowerCase());
      });
    }
    if (!isNullOrEmpty(max_size)) {
      results = results.filter(function(property) {
        return property.lotSize <= max_size;
      });
    }
    if (!isNullOrEmpty(min_size)) {
      results = results.filter(function(property) {
        return property.price >= min_price;
      });
    }
    if (!isNullOrEmpty(max_price)) {
      results = results.filter(function(property) {
        return property.price <= max_price;
      });
    }
    if (!isNullOrEmpty(min_price)) {
      results = results.filter(function(property) {
        return property.price >= min_price;
      });
    }
    if (!isNullOrEmpty(bedrooms)) {
      results = results.filter(function(property) {
        return property.bedrooms == bedrooms;
      });
    }
    if (!isNullOrEmpty(bathrooms)) {
      results = results.filter(function(property) {
        return property.bathrooms == bathrooms;
      });
    }
  }

  return (
    <div>
      <form id="search-form" method="GET">
        <label htmlFor="address">Address</label>
        <input name="address" id="address" placeholder="Address" type="text" onKeyUp={(event) => addressCheck(event)}/>
        <label htmlFor="min_price">Minimum Price</label>
        <input name="min_price" id="min_price" placeholder="Minimum price" type="number" onKeyUp={(event) => checkIfValueNegative(event)} />
        <label htmlFor="max_price">Maximum Price</label>
        <input name="max_price" id="max_price" placeholder="Maximum price" type="number" onKeyUp={(event) => checkIfValueNegative(event)}/>
        <label htmlFor="numBedrooms"># of Bedrooms</label>
        <input id="numBedrooms" name="numBedrooms" placeholder="# of bedrooms (1 to 5)" type="number" onKeyUp={(event) => bedroomsCheck(event)} />
        <label htmlFor="numBathrooms"># of Bathrooms</label>
        <input id="numBathrooms" name="numBathrooms" placeholder="# of bathrooms (1 to 5)" type="number" onKeyUp={(event) => bathroomsCheck(event)} />
        <label htmlFor="min_price">Minimum Lot Size</label>
        <input name="min_size" id="min_size" placeholder="Minimum Lot Size (sqft.)" type="number" onKeyUp={(event) => checkIfValueNegative(event)} />
        <label htmlFor="max_price">Maximum Lot Size</label>
        <input name="max_size" id="max_size" placeholder="Maximum Lot Size (sqft.)" type="number" onKeyUp={(event) => checkIfValueNegative(event)}/>
        <div id="errorMessage">{errorMessage}</div>
        <button type="submit" disabled={isSearchButtonDisabled}> Search </button>
      </form>
    </div>
  )
}

export default PropertySearchForm
