import React from 'react'
import { minPriceTracker, maxPriceTracker, errorMessage, isSearchButtonDisabled } from '../importantVariables.js'

function isNullOrEmpty(stringInput) {
  if (stringInput === null || stringInput.toString().trim() === "") {
    return true;
  } else {
    return false;
  }
}

