import React from 'react';
import {useState} from 'react';

function NonNegativeField(props) {
  [num, setNum] = useState();

  var checkIfNegative = function() {
    if (num && num < 0) {
      num = 0.00;
    }
  }
  return (
    <input
        id={props.field_id}
        placeholder={props.placeholder_text}
        type="number"
        value={num ? num : ""}
        onChange={checkIfNegative}
    />
  )
}

export default NonNegativeField
